const init = () => {

  window.addEventListener('mouseover', ({
    target
  }) => {
    const parent = target.parentElement
    if (target.tagName == 'IMG' && parent.dataset?.testid === "tweetPhoto") {
      const dom = target || {}
      const link = dom.src || ''
      console.log('parent, ', link)

    }

  })
}


setTimeout(() => {
  init()
}, 1500);